mapper.select:
select a2.*, IFNULL(a3.cnt,0) as cnt, IFNULL(a3.sendTime,'-') as sendTime from tab_user_nurse_relation a1 inner join tab_user_info a2 on a2.userId=a1.userId left join ( select b1.senderId, b1.receiverId, MAX(b1.sendTime) as sendTime, count(1) as cnt from tab_msg b1 where b1.status = 0 group by b1.senderId, b1.receiverId ) a3 on a3.receiverId=a1.nurseId and a1.userId=a3.senderId where a1.nurseId = #{nurseId}
AND a2.name LIKE CONCAT('%', #{name}, '%')
order by IFNULL(a3.cnt,0) desc

select a1.activityId, a1.activityType, a1.activityName, a1.activityContent, a1.activityCreatorId, a1.activityCreateTime, a1.activityStartDate, a1.activityDuration, a1.activityMemo, a1.activityIcon from tab_activity a1 where a1.activityCreatorId = #{nurseId}
AND a1.activityName LIKE CONCAT('%', #{name}, '%')
order by activityStartDate desc limit #{startNum}, #{rows}

	
		SELECT a1.*
		FROM tab_service a1
		where a1.serviceCreatorId=#{nurseId}
	
        
			AND a1.serviceName LIKE CONCAT('%', #{name}, '%') 
        
	
		order by a1.serviceStartDate
		LIMIT #{startNum}, #{rows}


	select a1.*
	from tab_service a1
	where a1.id = #{id}

mapper.update:

	UPDATE tab_user_bind_nurse_request  SET  
	status = #{status},
	responseTime = #{responseTime}
	WHERE id = #{id}

mapper.select:
select count(1) as cnt from tab_doctor a1 left join tab_hospital a2 on a1.doctorHospitalld=a2.hospitalId left join tab_hospital_dept a3 on a3.deptId=a1.doctorDeptld
AND a1.doctorHospitalld = #{hospitalId}
AND a1.doctorDeptld = #{deptId}
AND a1.doctorName LIKE CONCAT('%', #{name}, '%')

select a1.doctorId, a1.doctorName, a1.doctorGender, a1.doctorPhoto, a1.doctorHospitalld, a1.doctorDeptld, a1.doctorTitle, a1.doctorEducation, a1.doctorSkill, a1.doctorDetailDescriptior, a2.hospitalName, a2.hospitalAddr, a3.deptName from tab_doctor a1 left join tab_hospital a2 on a1.doctorHospitalld=a2.hospitalId left join tab_hospital_dept a3 on a3.deptId=a1.doctorDeptld
AND a1.doctorHospitalld = #{hospitalId}
AND a1.doctorDeptld = #{deptId}
AND a1.doctorName LIKE CONCAT('%', #{name}, '%')
order by a1.doctorName LIMIT #{startNum}, #{rows}
