mapper.select:
select a2.*, IFNULL(a3.cnt,0) as cnt, IFNULL(a3.sendTime,'-') as sendTime from tab_user_nurse_relation a1 inner join tab_user a2 on a2.userId=a1.userId left join ( select b1.senderId, b1.receiverId, MAX(b1.sendTime) as sendTime, count(1) as cnt from tab_msg b1 where b1.status = 0 group by b1.senderId, b1.receiverId ) a3 on a3.receiverId=a1.nurseId and a1.userId=a3.senderId where a1.nurseId = #{nurseId}
AND a2.name LIKE CONCAT('%', #{name}, '%')
order by IFNULL(a3.cnt,0) desc

select a1.activityId, a1.activityType, a1.activityName, a1.activityContent, a1.activityCreatorId, a1.activityCreateTime, a1.activityStartDate, a1.activityDuration, a1.activityMemo, a1.activityIcon from tab_activity a1 where a1.activityCreatorId = #{nurseId}
AND a1.activityName LIKE CONCAT('%', #{name}, '%')
order by activityStartDate desc limit #{startNum}, #{rows}

	
		SELECT a1.*
		FROM tab_service a1
		where a1.serviceCreatorId=#{nurseId}
	
        
			AND a1.serviceName LIKE CONCAT('%', #{name}, '%') 
        
	
		order by a1.serviceStartDate
		LIMIT #{startNum}, #{rows}


	select a1.*
	from tab_service a1
	where a1.id = #{id}

mapper.update:

	UPDATE tab_user_bind_nurse_request  SET  
	status = #{status},
	responseTime = #{responseTime}
	WHERE id = #{id}

mapper.select:
select count(1) as cnt from tab_doctor a1 left join tab_hospital a2 on a1.doctorHospitalId=a2.hospitalId left join tab_hospital_dept a3 on a3.deptId=a1.doctorDeptId
AND a1.doctorHospitalId = #{hospitalId}
AND a1.doctorDeptId = #{deptId}
AND a1.doctorName LIKE CONCAT('%', #{name}, '%')

select a1.doctorId, a1.doctorName, a1.doctorGender, a1.doctorPhoto, a1.doctorHospitalId, a1.doctorDeptId, a1.doctorTitle, a4.name as doctorEducationName, a1.doctorSkill, a1.doctorDetailDescriptior, a2.hospitalName, a2.hospitalAddr, a3.deptName from tab_doctor a1 left join tab_hospital a2 on a1.doctorHospitalId=a2.hospitalId left join tab_hospital_dept a3 on a3.deptId=a1.doctorDeptId left join tab_dict_education a4 on a4.id=a1.doctorEducation
AND a1.doctorHospitalId = #{hospitalId}
AND a1.doctorDeptId = #{deptId}
AND a1.doctorName LIKE CONCAT('%', #{name}, '%')
order by a1.doctorName LIMIT #{startNum}, #{rows}


	select a1.*
	from tab_user a1
	where a1.userId=#{userId}

mapper.update:

	UPDATE tab_user  SET  
	workingAge = #{workingAge},
	salary = #{salary},
	companyName = #{companyName},
	companyAddress = #{companyAddress}
	WHERE userId = #{userId}

mapper.select:

	select a1.*
	from tab_user a1
	where a1.userId=#{id}

SELECT COUNT(1) AS CNT FROM tab_doctor_advice a1 INNER JOIN tab_dict_doctor_advice_type a2 on a1.adviceTypId=a2.typeId where a1.userId=#{userId}
AND a1.adviceTime >= STR_TO_DATE(#{startTime},'%Y-%m-%d %H:%i:%s')
AND a1.adviceTime <= STR_TO_DATE(#{endTime},'%Y-%m-%d %H:%i:%s')

SELECT a1.id, a1.userId, a1.adviceTime, a1.adviceContent, a1.adviceTypId, a2.typeName FROM tab_doctor_advice a1 INNER JOIN tab_dict_doctor_advice_type a2 on a1.adviceTypId=a2.typeId where a1.userId=#{userId}
AND a1.adviceTime >= STR_TO_DATE(#{startTime},'%Y-%m-%d %H:%i:%s')
AND a1.adviceTime <= STR_TO_DATE(#{endTime},'%Y-%m-%d %H:%i:%s')
order by a1.adviceTime desc LIMIT #{startNum}, #{rows}

mapper.update:

	UPDATE tab_doctor_advice  SET  
	userId = #{userId},
	adviceTypId = #{typeId},
	adviceTime = #{adviceTime},
	adviceContent = #{adviceContent}
	WHERE id = #{id}
