mapper.select:

	SELECT A.TYPE_ID AS msgKey, A.TYPE_NAME AS msgVal
	FROM TAB_NEWS_TYPE A
	WHERE A.CUSTOM_ID = #{customId}


	SELECT A.TYPE_ID AS msgKey, A.TYPE_NAME AS msgVal
	FROM TAB_CASE_TYPE A
	WHERE A.CUSTOM_ID = #{customId}


	SELECT A.TYPE_ID AS msgKey, A.TYPE_NAME AS msgVal
	FROM TAB_PRODUCE_TYPE A
	WHERE A.CUSTOM_ID = #{customId}
